import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.changeStatus = this.changeStatus.bind(this);
    this.state = {
      employes:[{
        name:'Jatin',
        completed:false
      },
      {
        name:'Sanju',
        completed:true
      },
      {
        name:'Shiv',
        completed:false
      },
      {
        name:'Ravi',
        completed:false
      }
    ]
    }
  }
  changeStatus(index){
    console.log(this.state.employes[index]);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <ul className="info-user">
            {
              this.state.employes.map((employe, index) => {
                return <TestingList key={employe.name} clickHandler={this.changeStatus} index={index} info={employe}/>
              })
            }
          </ul>
        </header>
      </div>
    );
  }
}

class TestingList extends React.Component{
  render(){
    return (
      <li>
        <a href="#" onClick={ () => {
          this.props.clickHandler(this.props.index)
        }} className={this.props.info.completed ? 'active' : ''}>
        {this.props.info.name}
        </a>
      </li>
    )
  }
}

export default App;
