import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.changeStatus = this.changeStatus.bind(this);
    this.state = {
      employes:[{
        name:'Jatin',
        completed:false
      },
      {
        name:'Sanju',
        completed:false
      },
      {
        name:'Shiv',
        completed:false
      },
      {
        name:'Ravi',
        completed:false
      }
    ]
    }
  }
  changeStatus(index){
    var employes = this.state.employes;
    var employe = employes[index];
    employe.completed = !employe.completed;
    this.setState({
      employes:employes
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <ul className="info-user">
            {
              this.state.employes.map((employe, index) => {
                return <TestingList key={employe.name} clickHandler={this.changeStatus} index={index} info={employe}/>
              })
            }
          </ul>
        </header>
      </div>
    );
  }
}

export default App;
